import React, { Component } from "react";
import Course from "./Course";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";

export class CourseList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: true,
      articles: [],
    };
  }

  componentDidMount() {
    fetch(
      "https://api.nytimes.com/svc/books/v3/lists/current/hardcover-fiction.json?api-key=YW7v4jYO5anNGpDXSmDXZhswezl4w6yZ"
    )
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        let result = data.response.docs;
        this.setState({ articles: result });
      });
  }
  render() {
    return (
      <div>
        {this.state.isLoaded} ? <div>loading...</div> :
        <div>
          <TextField
            style={{ padding: "24px" }}
            id={"searchInput"}
            placeholder={"Search article..."}
            margin={"normal"}
          ></TextField>
          <Course articles={this.state.articles} />
        </div>
      </div>
    );
  }
}

export default CourseList;
